","keccak256":"0x79004a1d1471abe87e92c4497acba06d5dfa5b1f3e9894a0991a7c4669c5c02c","license":"MIT"},"contracts/exchanges/interfaces/IETHtxAMM.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.7.6;

interface IETHtxAMM {
	/* Views */

	function cRatio()
		external
		view
		returns (uint256 numerator, uint256 denominator);

	function cRatioBelowTarget() external view returns (bool);

	function ethForEthtx(uint256 amountETHtxOut) external view returns (uint256);

	function ethFromEthtxAtRedemption(uint256 amountETHtxIn)
		external
		view
		returns (uint256);

	function ethtx() external view returns (address);

	function ethtxFromEth(uint256 amountETHIn) external view returns (uint256);

	function ethtxForEthAtRedemption(uint256 amountETHOut)
		external
		view
		returns (uint256);

	function ethSupply() external view returns (uint256);

	function ethtxAvailable() external view returns (uint256);

	function ethtxOutstanding() external view returns (uint256);

	function feeLogic() external view returns (address);

	function gasOracle() external view returns (address);

	function gasPerETHtx() external pure returns (uint256);

	function gasPrice() external view returns (uint256);

	function gasPriceAtRedemption() external view returns (uint256);

	function maxGasPrice() external view returns (uint256);

	function targetCRatio()
		external
		view
		returns (uint128 numerator, uint128 denominator);

	function weth() external view returns (address);

	/* Mutators */

	function buy(uint256 deadline) external payable;

	function buyWithWETH(uint256 amountIn, uint256 deadline) external;

	function buyExact(uint256 amountOut, uint256 deadline) external payable;

	function buyExactWithWETH(
		uint256 amountInMax,
		uint256 amountOut,
		uint256 deadline
	) external;

	function buyWithExactETH(uint256 amountOutMin, uint256 deadline)
		external
		payable;

	function buyWithExactWETH(
		uint256 amountIn,
		uint256 amountOutMin,
		uint256 deadline
	) external;

	function pause() external;

	function recoverUnsupportedERC20(
		address token,
		address to,
		uint256 amount
	) external;

	function redeem(uint256 amountIn, uint256 deadline) external;

	function redeemExact(
		uint256 amountInMax,
		uint256 amountOut,
		uint256 deadline
	) external;

	function redeemWithExact(
		uint256 amountIn,
		uint256 amountOutMin,
		uint256 deadline
	) external;

	function setEthtx(address account) external;

	function setGasOracle(address account) external;

	function setTargetCRatio(uint128 numerator, uint128 denominator) external;

	function setWETH(address account) external;

	function unpause() external;

	/* Events */

	event ETHtxSet(address indexed author, address indexed account);
	event GasOracleSet(address indexed author, address indexed account);
	event RecoveredUnsupported(
		address indexed author,
		address indexed token,
		address indexed to,
		uint256 amount
	);
	event TargetCRatioSet(
		address indexed author,
		uint128 numerator,
		uint128 denominator
	);
	event WETHSet(address indexed author, address indexed account);
}
","keccak256":"0xe3319fc2b8380a33fa62691b20e54ff95106daaaedad5c4d34dc56b70c5e064b","license":"MIT"},"contracts/rewards/ETHtxRewardsManager.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.7.6;

import "../exchanges/interfaces/IETHtxAMM.sol";
import "./interfaces/IETHtxRewardsManager.sol";
import "./interfaces/IETHmxRewards.sol";
import "./interfaces/ILPRewards.sol";
import "./RewardsManager.sol";

contract ETHtxRewardsManager is RewardsManager, IETHtxRewardsManager {
	using EnumerableSet for EnumerableSet.AddressSet;
	using SafeERC20 for IERC20;
	using SafeMath for uint256;

	/* Mutable Private State */

	address private _ethmxRewards;
	address private _ethtx;
	address private _ethtxAMM;
	address private _lpRewards;

	/* Constructor */

	constructor(
		address owner_,
		address defaultRecipient_,
		address rewardsToken_
	) RewardsManager(defaultRecipient_, rewardsToken_) {
		if (owner_ != owner()) {
			transferOwnership(owner_);
		}
	}

	/* External Mutators */

	function convertETHtx() public override {
		IERC20 ethtxHandle = IERC20(ethtx());
		uint256 amount = ethtxHandle.balanceOf(address(this));
		if (amount == 0) {
			return;
		}

		address ethtxAMM_ = ethtxAMM(); // Gas savings
		ethtxHandle.safeIncreaseAllowance(ethtxAMM_, amount);

		// solhint-disable-next-line not-rely-on-time
		uint256 deadline = block.timestamp + 3600;
		IETHtxAMM(ethtxAMM_).redeem(amount, deadline);
	}

	function distributeRewards() external override returns (uint256) {
		convertETHtx();
		uint256 rewards = sendRewards();
		if (rewards != 0) {
			notifyRecipients();
		}
		return rewards;
	}

	function notifyRecipients() public override {
		_notifyEthmxRewards();
		_notifyLpRewards();
	}

	function sendRewards() public override returns (uint256) {
		uint256 rewards = _currentRewardsBalance();
		if (rewards == 0) {
			return 0;
		}

		uint256 totalShares_ = totalShares();

		for (uint256 i = 0; i < _recipients.length(); i++) {
			_sendTo(_recipients.at(i), totalShares_, rewards);
		}

		_totalRewardsRedeemed += rewards;
		return rewards;
	}

	function setEthmxRewardsAddress(address addr) public override onlyOwner {
		_ethmxRewards = addr;
		emit EthmxRewardsAddressSet(_msgSender(), addr);
	}

	function setEthtxAddress(address addr) public override onlyOwner {
		_ethtx = addr;
		emit EthtxAddressSet(_msgSender(), addr);
	}

	function setEthtxAMMAddress(address addr) public override onlyOwner {
		_ethtxAMM = addr;
		emit EthtxAMMAddressSet(_msgSender(), addr);
	}

	function setLPRewardsAddress(address addr) public override onlyOwner {
		_lpRewards = addr;
		emit LPRewardsAddressSet(_msgSender(), addr);
	}

	/* Public Views */

	function ethmxRewards() public view override returns (address) {
		return _ethmxRewards;
	}

	function ethtx() public view override returns (address) {
		return _ethtx;
	}

	function ethtxAMM() public view override returns (address) {
		return _ethtxAMM;
	}

	function lpRewards() public view override returns (address) {
		return _lpRewards;
	}

	/* Internal Mutators */

	function _notifyEthmxRewards() internal {
		IETHmxRewards ethmxRewardsHandle = IETHmxRewards(ethmxRewards());
		if (ethmxRewardsHandle.readyForUpdate()) {
			ethmxRewardsHandle.updateAccrual();
		}
	}

	function _notifyLpRewards() internal {
		ILPRewards(lpRewards()).updateAccrual();
	}

	function _sendTo(
		address account,
		uint256 totalShares_,
		uint256 totalRewards
	) internal {
		Shares storage s = _shares[account];
		uint256 amount = totalRewards.mul(s.active).div(totalShares_);

		IERC20(rewardsToken).safeTransfer(account, amount);
	}
}
